package day12;

public class Array15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//p15
		//System.out.println(0);~(10);까지 구문을 동작할 수 있도록 for를 작성
		int[] a = new int[10];
		int i = 0;
		for (i = 0; i < 10; i++)
		{
			a[i] = i;
			System.out.println(a[i]);
		
		}


	}

}
