package day08;

public class Control_if_hw02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//만약 i가 4 또는 5이면 "*"를 출력하시오
		for (int i= 0; i < 10; i++) {
			if (i == 4 || i == 5) {
				System.out.println("*");
			}
			System.out.println(i);
		}

	}

}
